import { Component, Input } from '@angular/core';

@Component({
  selector: 'app-curso',
  templateUrl: './input-property.component.html',
  styleUrls: ['./input-property.component.css']
  inputs
})
export class InputPropertyComponent {
  @Input('nome')
  nomeDoCurso: string = ''
}
