import { Component } from '@angular/core';

@Component({
  selector: 'app-data-binding',
  templateUrl: './data-binding.component.html',
  styleUrls: ['./data-binding.component.css']
})
export class DataBindingComponent {
  url: string = 'http://joao.com.br'
  cursoAngular: boolean = true
  urlImagem = 'http://lorempixel.com.br/500/400/?1'
  getValor() {
    return 1
  }
  getCurtirCurso() {
    return true
  }
}
<section class="property-binding">
  <article>
    <h3>Interpolation</h3>
    <p>String renderizada {{url}}</p>
    <p>1 + 1 = {{1+1}}</p>
    <p>1 + 1 não é {{1+1+getValor()}}</p>
    <p>Curso de Angular e gosto do curso: {{cursoAngular && getCurtirCurso()}}
    </p>
    <img src="{{urlImagem}}" alt="">
    <img [src]="urlImagem" alt="">
  </article>
</section>
